mapper.select:
select
    RECORD_ID,
    USER_NAME,
    ACCESS_TYPE,
    ACCESS_TIME,
    ACCESS_IP
from
    wms_access_record

    
        USER_NAME = #{userName}
    
    
        and ACCESS_TYPE = #{accessType}
    
    
        and ACCESS_TIME >= #{startDate}
    
    
        and ACCESS_TIME <= #{endDate}
